import React from 'react';
import { useState, useEffect } from 'react';
import instance from '../Axios/axios';

const base_URL = "https://image.tmdb.org/t/p/orignal/"

function AllRows(props) {
    const  [movies, setMovies] = useState([]);

    useEffect(()=>{
      let mounted= true;
         if(mounted){
              async function fetchData () {
                     const request = await instance.get(props.fetchURL);
                     setMovies(request.data.results);
                     console.log(request.data.results);
                     return request;
              }

              fetchData()
         }
      return()=>{
          mounted=false;
      }
    },[props.fetchURL]);
    
    console.log(instance)
    return (
        <div className='row_main_container'>
             <h3>{props.title}</h3> 
             
             <div className="row_poster">
                 {
                     movies.map((movie)=>(
                         <div className="poster_container" key={movie.id}>
                             <img src={`${base_URL}${movie.poster_path}`} alt="ss" />
                         </div>

                     ))
                 }
             </div>
        </div>
    )
}

export default AllRows;
